com.mirth.connect.plugins.javascriptstep.script:
if(msg['PID']['PID.3']['PID.3.4'].toString() !== 'XHISARR')
{
 	if(msg['OBR']['OBR.4']['OBR.4.1'] == 'CARDHOLT3')
	{
 		if (msg['OBR']['OBR.20']['OBR.20.1'] == 'CARDHOLTP')
 		{
				msg['OBR']['OBR.4']['OBR.4.1'] = 'R' + msg['OBR']['OBR.20']['OBR.20.1'].copy();
 				msg['OBX']['OBX.3']['OBX.3.1'] = 'R' + msg['OBR']['OBR.20']['OBR.20.1'].copy();
 	 	} else { 
 				msg['OBR']['OBR.4']['OBR.4.1'] ='RCARDHOLT3'
 				msg['OBX']['OBX.3']['OBX.3.1']='RCARDHOLT3'
 			}
	}

 if(msg['OBR']['OBR.4']['OBR.4.1'] == 'CARDMAPA7')
 {
 	msg['OBR']['OBR.4']['OBR.4.1'] = 'RCARDMAPA7'
 	msg['OBX']['OBX.3']['OBX.3.1'] = 'RCARDMAPA7'
 }
}

if(msg['PID']['PID.3']['PID.3.4'].toString() == 'XHISARR')
{

	if(msg['OBR']['OBR.4']['OBR.4.1'] == 'CARDHOLT3')
 		{
 			msg['OBR']['OBR.4']['OBR.4.1'] = msg['OBR']['OBR.20']['OBR.20.1'].copy();
 			msg['OBX']['OBX.3']['OBX.3.1'] = msg['OBR']['OBR.20']['OBR.20.1'].copy();
 		}

	if(msg['OBR']['OBR.4']['OBR.4.1'] == 'CARDMAPA7')
 		{
		 	msg['OBR']['OBR.4']['OBR.4.1'] = msg['OBR']['OBR.20']['OBR.20.1'].copy();
 			msg['OBX']['OBX.3']['OBX.3.1'] = msg['OBR']['OBR.20']['OBR.20.1'].copy();
		}

	if(msg['OBR']['OBR.4']['OBR.4.1'] == '11524-0')
		{
			msg['OBR']['OBR.4']['OBR.4.1'] = msg['OBR']['OBR.20']['OBR.20.1'].copy();
			msg['OBX']['OBX.3']['OBX.3.1'] = msg['OBR']['OBR.20']['OBR.20.1'].copy();
		}
}

