com.mirth.connect.plugins.javascriptstep.script:
// Em PID 3.4 virá sempre o identificador XHISHSP. 
// Para criar a ORG no sentinel, constroi-se a string com XHIS + MSH4.1
//if(msg['PID']['PID.3']['PID.3.4'].toString() != 'XHISARR')
//{
//	msg['PID']['PID.3']['PID.3.4'].toString() == 'XHIS' + msg['MSH']['MSH.4']['MSH.4.1'].toString()
//}

//msg['PID']['PID.3']['PID.3.4'].toString() = 'XHIS' + msg['MSH']['MSH.4']['MSH.4.1'].toString()

msg['PID']['PID.3']['PID.3.4'] = msg['MSH']['MSH.4']['MSH.4.1'].toString()

if(msg['PID']['PID.3']['PID.3.4'].toString() == 'ARR') //exames Arrabida
{
	//Exames de Holter
	if(msg['OBR']['OBR.4']['OBR.4.1'] == '09000007')
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDHOLT3';
	}

	if(msg['OBR']['OBR.4']['OBR.4.1'] == '09000009')
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDHOLT3';
	}	

	if(msg['OBR']['OBR.4']['OBR.4.1'] == '09000008')
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDHOLT3';
	}

	if(msg['OBR']['OBR.4']['OBR.4.1'] == '09000099')
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDHOLT3';
	}

	if(msg['OBR']['OBR.4']['OBR.4.1'] == '09010007')
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDHOLT3';
	}

	if(msg['OBR']['OBR.4']['OBR.4.1'] == '09000013HA') //Registador de Eventos - 7 dias
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDHOLT3';
	}

	if(msg['OBR']['OBR.4']['OBR.4.1'] == '09000014HA') //Registador de Eventos - 15 dias
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDHOLT3';
	}

	if(msg['OBR']['OBR.4']['OBR.4.1'] == '09000019HA') //Holter - 12 derivações
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDHOLT3';
	}

	//Exames de MAPA
	if(msg['OBR']['OBR.4']['OBR.4.1'] == '09000201')
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDMAPA7';
	}

	if(msg['OBR']['OBR.4']['OBR.4.1'] == '09000299')
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDMAPA7';
	}
	//Exames de ECG
	if(msg['OBR']['OBR.4']['OBR.4.1'] == '09000003')
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = '11524-0';
 	}

 	if(msg['OBR']['OBR.4']['OBR.4.1'] == '09009904')
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = '11524-0';
 	}

 	if(msg['OBR']['OBR.4']['OBR.4.1'] == '090000031')
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = '11524-0';
 	}

 	if(msg['OBR']['OBR.4']['OBR.4.1'] == '09010003')
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = '11524-0';
 	}

 	if(msg['OBR']['OBR.4']['OBR.4.1'] == 'EIS00007')
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = '11524-0';
 	} 

 	if(msg['OBR']['OBR.4']['OBR.4.1'] == 'M009000009') //ECG Rastreio
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = '11524-0';
 	} 

 	//Exames de Prova de esforço
	if(msg['OBR']['OBR.4']['OBR.4.1'] == '09000005') //Prova Esforco maxima ou Submaxima em Tapete Rolante
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDPE002';
 	}	

 	if(msg['OBR']['OBR.4']['OBR.4.1'] == '09010005') //Prova esforco Ped. maxima,submaxima tapete rolante
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDPE002';
 	}

 	if(msg['OBR']['OBR.4']['OBR.4.1'] == 'EIS00017') //ECG Esforço (EIS/Vida/Rotina)
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDPE002';
 	} 		
}

if(msg['PID']['PID.3']['PID.3.4'].toString() != 'ARR') //exames HOSPOR
{
	//Exames de prova de esforço
	if(msg['OBR']['OBR.4']['OBR.4.1'] == 'O009000005') //PROVA DE ESFORÇO MÁXIMA/SUBMÁXIMA EM TAPETE ROLANTE
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDPE002';
 	}	

 	//Exames de Holter
	if(msg['OBR']['OBR.4']['OBR.4.1'] == 'O009000007') //MONITORIZAÇÃO ELECTROCARDIOGRÁFICA CONTÍNUA PROLONGADA (HOLTER) 
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDHOLT3';
	}

	if(msg['OBR']['OBR.4']['OBR.4.1'] == 'O009010007') //MONITORIZAÇÃO ELECTROCARDIOGRÁFICA CONTÍNUA PROLONGADA P/MÉTODO HOLTER
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDHOLT3';
	}	

	if(msg['OBR']['OBR.4']['OBR.4.1'] == 'O009000009') //MONITORIZAÇÃO ELECTROCARDIOGRÁFICA PROLONGADA COM REGISTO EVENTOS (..)
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDHOLT3';
	}

	if(msg['OBR']['OBR.4']['OBR.4.1'] == 'M009000003') //REGISTADOR DE EVENTOS - 3 DIAS
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDHOLT3';
	}

	if(msg['OBR']['OBR.4']['OBR.4.1'] == 'M009000004') //REGISTADOR DE EVENTOS - 7 DIAS
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDHOLT3';
	}

	//Exames de MAPA
	if(msg['OBR']['OBR.4']['OBR.4.1'] == 'O009000201') //REGISTO AMBULATÓRIO PROLONGADO (24H OU MAIS) DA PRESSÃO ARTERIAL
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = 'CARDMAPA7';
	}

	//Exames de ECG
	if(msg['OBR']['OBR.4']['OBR.4.1'] == 'M009000007') //ELECTROCARDIOGRAMA SIMPLES 12 DERIV. C/ INT./ RELAT. (EM CONSULTA) 
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = '11524-0';
 	}

	if(msg['OBR']['OBR.4']['OBR.4.1'] == 'O009000003') //ELECTROCARDIOGRAMA SIMPLES DE 12 DERIVAÇÕES C/INTERPRETAÇÃO+RELATÓRIO 
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = '11524-0';
 	}	

	if(msg['OBR']['OBR.4']['OBR.4.1'] == 'O009010003') //ELECTROCARDIOGRAMA SIMPLES DE 12 DERIVAÇÕES C/INTERPRETAÇÃO/RELATÓRIO 
	{
 		msg['OBR']['OBR.4']['OBR.4.1'] = '11524-0';
 	}	 	 	

}

