com.mirth.connect.plugins.javascriptstep.script:
//
//  larry edited this
//
/*
 OnBase Base64 OBX only ip address  10.196.16.202  port number   33316		
 to Epic without Base64 PDF 10.196.20.245  12222 
*/

// Remove the Base64 PDF OBX segment only

var obxidx = -1;

// Remove only OBX segments containing 'ED' as their identifer 

for each (seg in msg.children()) 
	if (seg.name().toString() == 'OBX') 
	{
		obxidx++;
		var obx_val = seg['OBX.2']['OBX.2.1'].toString()
		if (obx_val == 'ED') 
		{
			delete msg['OBX'][obxidx];
			obxidx--;
		}
	}
// resequence OBX lines
var OBXCounter =1;
for each (seg in msg..OBX)
{
	seg['OBX.1']['OBX.1.1']=OBXCounter;
	OBXCounter++;
}
// remove delete msg['TXA'];
for(var i = 0; i<msg['TXA'].length();i++)
{
delete msg['TXA'][i];
i--;
}

//
//  larry edited this
//
/*
 OnBase Base64 OBX only ip address  10.196.16.202  port number   33316		
 to Epic without Base64 PDF 10.196.20.245  12222 
*/

// Remove the Base64 PDF OBX segment only

var obxidx = -1;

// Remove only OBX segments containing 'ED' as their identifer 

for each (seg in msg.children()) 
	if (seg.name().toString() == 'OBX') 
	{
		obxidx++;
		var obx_val = seg['OBX.3']['OBX.3.1'].toString()
		if ( obx_val != 'MDM') 
		{
			delete msg['OBX'][obxidx];
			obxidx--;
		}
	}
// resequence OBX lines
var OBXCounter =1;
for each (seg in msg..OBX)
{
	seg['OBX.1']['OBX.1.1']=OBXCounter;
	OBXCounter++;
}
// remove delete msg['NTE'];
for(var i = 0; i<msg['NTE'].length();i++)
{
delete msg['NTE'][i];
i--;
}
// change message type to MDM^T02
msg['MSH']['MSH.9']['MSH.9.1']='MDM';
msg['MSH']['MSH.9']['MSH.9.2']='T02';

msg['MSH']['MSH.5']['MSH.5.1']='ONBASE';
msg['MSH']['MSH.6']['MSH.6.1']='ONBASE';
