// change message type to MDM^T02
msg['MSH']['MSH.9']['MSH.9.1']='MDM';
msg['MSH']['MSH.9']['MSH.9.2']='T02';

// remove delete msg['NTE'];
for(var i = 0; i<msg['NTE'].length();i++)
{
delete msg['NTE'][i];
i--;
}

var deleteSeg = false;
for each(seg in msg.children()) {
	if(seg.name() == "OBX") {
var strOBRUSID = seg['OBX.2']['OBX.2.1'].toString();
var strOBRLR = seg['OBX.2']['OBX.2.1'].toString();
		if(strOBRUSID== "TX" || strOBRLR== "NM"){
			deleteSeg = true;	
		} else if(strOBRUSID.substring(0,5) == "TX" || strOBRLR.substring(0,5) == "NM" || strOBRUSID.substring(0,4) == "TX"){
			deleteSeg = false;
		}
	}
		if(deleteSeg == true ){
		var segType = seg.name();
		var segCnt = 0;
		for(var i=0;i<seg.childIndex();i++){
			if(msg.children()[i].name() == seg.name()) {
				segCnt++;
			}
		}
		delete msg[segType][segCnt];
	}}

// resequence OBX Statements
